mapper.select:
select g.code,g.membercode,g.statuscode,g.deadline,g.total,g.min,g.title,g.detail,
g.productname,g.productprice,g.boardkindcode,g.regdate,g.moddate,g.deldate,m.businessid 
from groupbuying g join member m on g.membercode = m.code where g.code = #{code}

mapper.update:
update groupbuying set
deadLine=#{deadLine},min=${min},title=#{title},detail=#{detail},productName=#{productName},productPrice=#{productPrice}, moddate=sysdate
where code=${code}

mapper.select:
select g.code as groupbuyingcode , g.membercode as businesscode , g.statuscode, g.deadline, g.title, g.productname, g.productprice, g.regdate, m.companyname, count(r.parentcode) as applycount, nvl(sum(r.quantity),0) as applyquantity from groupbuying g left join member m on g.membercode = m.code left join requestlist r on r.parentcode = g.code where g.membercode = #{code}
and g.statuscode = ${statuscode} group by g.code, g.membercode, g.statuscode,g.deadline,g.title, g.productname, g.productprice,g.regdate,m.companyname, r.parentcode
group by g.code, g.membercode, g.statuscode,g.deadline,g.title, g.productname, g.productprice,g.regdate,m.companyname, r.parentcode
